const MP4_BASE64 = '';
const WEBM_BASE64 = '';

function decodeBase64ToUint8Array(base64) {
  if (typeof atob === 'function') {
    const binary = atob(base64);
    const length = binary.length;
    const bytes = new Uint8Array(length);
    for (let i = 0; i < length; i++) {
      bytes[i] = binary.charCodeAt(i);
    }
    return bytes;
  }
  if (typeof Buffer !== 'undefined') {
    const buffer = Buffer.from(base64, 'base64');
    return new Uint8Array(buffer.buffer, buffer.byteOffset, buffer.byteLength);
  }
  throw new Error('No base64 decoder available for launch media.');
}

function createObjectUrl(base64, type) {
  if (typeof URL === 'undefined' || typeof Blob === 'undefined') {
    return `data:${type};base64,${base64}`;
  }
  const bytes = decodeBase64ToUint8Array(base64);
  const blob = new Blob([bytes], { type });
  return URL.createObjectURL(blob);
}

function buildLaunchMediaSources() {
  return [
    { type: 'video/webm', url: createObjectUrl(WEBM_BASE64, 'video/webm') },
    { type: 'video/mp4', url: createObjectUrl(MP4_BASE64, 'video/mp4') },
  ];
}

let cachedSources = null;

function getLaunchMediaSources() {
  if (!cachedSources) {
    cachedSources = buildLaunchMediaSources();
  }
  return cachedSources;
}

function revokeLaunchMediaSources() {
  if (!cachedSources) return;
  if (typeof URL !== 'undefined' && typeof URL.revokeObjectURL === 'function') {
    cachedSources.forEach(entry => {
      if (entry && entry.url && entry.url.startsWith('blob:')) {
        try { URL.revokeObjectURL(entry.url); } catch (err) {}
      }
    });
  }
  cachedSources = null;
}

function publishLaunchMediaSources() {
  if (typeof window === 'undefined') return;
  const sources = getLaunchMediaSources();
  window.LAUNCH_MEDIA_SOURCES = sources;
  if (typeof window.dispatchEvent === 'function') {
    try {
      window.dispatchEvent(new CustomEvent('launch-media-ready', { detail: sources }));
    } catch (err) {
      if (typeof window.CustomEvent !== 'function' && typeof document !== 'undefined') {
        const legacyEvent = document.createEvent('CustomEvent');
        legacyEvent.initCustomEvent('launch-media-ready', false, false, sources);
        window.dispatchEvent(legacyEvent);
      }
    }
  }
  if (typeof window.__applyLaunchMediaSources === 'function') {
    try { window.__applyLaunchMediaSources(sources); } catch (err) {}
  }
}

publishLaunchMediaSources();

if (typeof window !== 'undefined') {
  window.addEventListener('beforeunload', () => revokeLaunchMediaSources(), { once: true });
}

export { getLaunchMediaSources, revokeLaunchMediaSources };

